// src/short-bind.ts
var import_api = require("@vue-macros/short-bind/api");
var plugin = () => {
  return {
    name: "vue-macros-short-bind",
    version: 1,
    resolveTemplateCompilerOptions(options) {
      options.nodeTransforms ||= [];
      options.nodeTransforms.push((0, import_api.transformShortBind)());
      return options;
    }
  };
};
module.exports = plugin;
